$(document).ready(function(){var path=window.location.pathname;var page=path.split("/").pop();if(page==="")page="index.html";$('.nav-link').removeClass('active');$('.nav-link[href="'+page+'"]').addClass('active');$('a[href^="#"]').on('click',function(event){var target=$(this.getAttribute('href'));if(target.length){event.preventDefault();$('html, body').stop().animate({scrollTop:target.offset().top-70},800)}});const observerOptions={root:null,rootMargin:'0px',threshold:0.1};const observer=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add('animate-fade-in-up');entry.target.style.opacity=1;observer.unobserve(entry.target)}})},observerOptions);$('.card, .timeline-item').each(function(){$(this).css('opacity','0');observer.observe(this)})});
